mapper.update:
update
  mistake_audio
set
  del_flg = 1
where del_flg = 0 and user_id = #{userId,jdbcType=VARCHAR}
  and cpsrcd_id = #{cpsrcdId,jdbcType=VARCHAR}

update
  mistake_audio
set
  error_sum = error_sum + 1
where del_flg = 0 and user_id = #{userId,jdbcType=VARCHAR}
  and cpsrcd_id = #{cpsrcdId,jdbcType=VARCHAR}
